			msg = data.err;
		}

		if (msg) {
			if ($toast) $toast.error(msg);
			if ($Progress) $Progress.fail();
			console.warn(msg);
			return false;
		} else {
			if ($Progress) $Progress.finish();
			return data;
		}
	},
	function (error) {
		// Do something with response error
		return Promise.reject(error);
	}
);

